<span class="line"><span style="color:#A6ACCD;">h1 = histogram(Tb.BMI(tf),</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">BinMethod</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">,</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">integers</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">);</span></span>
